import React, { useState } from "react";
import { View, StyleSheet, Text, Button, FlatList } from "react-native";
//import styles from "../config/styles";
import Constants from "expo-constants";
import MessageItem from '../components/MessageItem';
import ListItemSeparator from '../components/ListItemSeparator';
import ListItemDeleteAction from '../components/ListItemDeleteAction';
import PicIcon from '../components/PicIcon';
import TopNav from '../components/TopNav';

const initialMessages = [
  {
    id: 4,
    name: 'Angelina',
    message: 'Hsjdflksdlfjlsdkfjlkdflksdj;asdjfl;',
    time: '2:11 PM',
    image: { uri: 'https://notjustdev-dummy.s3.us-east-2.amazonaws.com/avatars/4.jpg' },
    numMessages: 0
  },
]
const UserHomeScreen = ({ navigation }) => {

  const [messages, setMessages] = useState(initialMessages);
  const [refreshing, setRefreshing] = useState(false);


  const handleDelete = (message) => {
    //delete the message from messages
    setMessages(messages.filter((m) => m.id !== message.id));
  };
  return (
    <View paddingTop={Constants.statusBarHeight}>
      <TopNav title="Matches" />
      <FlatList
        horizontal

        data={messages}
        keyExtractor={message => message.id.toString()}
        renderItem={({ item }) => (
          <PicIcon
            image={item.image} />
        )}>
      </FlatList>
      <FlatList
        ListHeaderComponent={() => <Text>Messages</Text>}
        data={messages}
        keyExtractor={message => message.id.toString()}
        renderItem={({ item }) => (
          <MessageItem

            name={item.name}
            message={item.message}
            image={item.image}
            time={item.time}
            numMessages={item.numMessages}
            onPress={() => console.log("Message selected", item)}
            renderRightActions={() =>
              <ListItemDeleteAction onPress={() => handleDelete(item)} />}
          />
        )}
        ItemSeparatorComponent={ListItemSeparator}
        refreshing={refreshing}
        onRefresh={() => {
          setMessages([
            {
              id: 2,
              name: 'Aurora',
              message: 'YOOO',
              time: '1:23 PM',
              image: require('../assets/girl2.png')
            },
          ])
        }}
      />
    </View>
  );
};

export default UserHomeScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#F8F4F1",
  },
});
